    private void bAvaliarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bAvaliarActionPerformed
        
        int x = Integer.parseInt(tfEixoX.getText());
        int y = Integer.parseInt(tfEixoY.getText());
        
        Ponto p = new Ponto();
        
        cbIncideX.setSelected(p.estaIncidindoSobreX());
        cbIncideY.setSelected(p.estaIncidindoSobreY());
        cbPri.setSelected(p.identificarQuadrante() == Quadrante.PRIMEIRO);
        cbSeg.setSelected(p.identificarQuadrante() == Quadrante.SEGUNDO);
        cbTer.setSelected(p.identificarQuadrante() == Quadrante.TERCEIRO);
        cbQua.setSelected(p.identificarQuadrante() == Quadrante.QUARTO);
        
    }//GEN-LAST:event_bAvaliarActionPerformed

